import { Component, OnDestroy, OnInit } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { Subscription } from 'rxjs';
import { IMovie } from '../../../../interfaces/movie';
import { MovieDataService } from '../../../../services/movie-data.service';
import { ModalComponent } from '../modal/modal.component';

@Component({
  selector: 'app-movie-card',
  templateUrl: './movie-card.component.html',
  styleUrls: ['./movie-card.component.scss'],
})
export class MovieCardComponent implements OnInit, OnDestroy {
  private movieCardSubscription!: Subscription;

  public movies: IMovie[] = [];

  constructor(public dialog: MatDialog, private movieData: MovieDataService) {}

  public ngOnInit(): void {
    this.movieCardSubscription = this.movieData
      .getMovieData()
      .subscribe((data) => {
        this.movies = data;
      });
  }

  public openModal(movie: IMovie) {
    const dialogRef = this.dialog.open(ModalComponent, {
      data: {
        title: `Detalhes do filme: ${movie.Title}`,
        imdbID: movie.imdbID,
      },
    });

    dialogRef.componentInstance.hasErrorEmitter.subscribe(() => {
      dialogRef.close();
    });
  }

  public ngOnDestroy(): void {
    this.movieCardSubscription.unsubscribe();
  }
}
<article class="grid-cards" *ngFor="let movie of movies">
  <div class="grid-cards__details">
    <h4 class="grid-cards__title">{{ movie.Title }}</h4>

    <img
      class="grid-cards__image"
      src="{{ movie.Poster }}"
      alt="{{ movie.Title }}"
    />
    <button class="grid-cards__button" type="button" (click)="openModal(movie)">
      Detalhes
    </button>
  </div>
</article>
